@use "../abstracts/index" as *;
:root,
:root.dark {
    --bg-color: rgb(39, 39, 39);
    --text-color: rgb(210, 212, 214);
}
:root.light{
    --bg-color: #d1d0d0;
    --text-color: rgb(0, 0, 0);
}
body{
    width: 95vw;
    height: 100vh;
    background-color: var(--bg-color);
    transition: all 0.3s;
    margin: auto;
    .wrapper{
        .top{
            display: flex;
            width: 100%;
            h1{
                color: var(--text-color);
            }
            .topBtns{
                cursor: pointer;
                margin-left: auto;
                font-size: 40px;
                ion-icon{
                    color: var(--text-color);
                }

            }
        }
        .modal-shadow{
            visibility: hidden;
            width: 100vw;
            height: 150vh;
            margin: -3%;
            background-color: rgba(0, 0, 0, 0.5);
            position: fixed;
            transition: opacity 0.3s ease-out;
            z-index: 1;
            .popup{
                z-index: 1;
                position: absolute;
                left: 40%;
                top: 35%;
                transform: translate(-33.3%, -33.3%);
                background-color: var(--bg-color);
                width: 50%;
                height: 25%;
                .popupText{
                    color: var(--text-color);
                    width: 100%;
                    height: 100%;
                    text-align: center;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    line-height: 2rem;
                    font-size: 20px;
                }
                .closePopup{
                    position: relative;
                    top: -25%;
                    left: 0%;
                    transform: translate(12%, 20%);
                    width: 80%;
                    line-height: 2rem;
                    border-radius: 15px;
                    background-color: teal;
                    color: var(--text-color);
                    border: 1px solid var(--text-color);
                    cursor: pointer;
                    font-size: 20px;
                    text-transform: uppercase;
                    transition: background-color 0.3s;
                }
                .closePopup:hover{
                    background-color: rgb(132, 0, 255);
                }
            }
        }
        .tableMain{
            border: 1px solid rgb(61, 61, 61);
            width: 100%;
            height: 300px;
            color: var(--text-color);
            display: flex;
            ul{
                margin: 0 auto;
                font-size: 20px;
                list-style: none;
                li{
                    margin: 5% 0;
                    cursor: pointer;
                }
                li:hover{
                    background-color: rgba(0, 0, 255, 0.5);
                }
                .activeEvidence{
                    text-decoration: underline;
                    background-color: blue;
                }
                .excludedEvidence{
                    text-decoration: line-through;
                    background-color: blue;
                }
            }
        }
        .ghostsTable{
            display: flex;
            flex-wrap: wrap;
            .ghostBox{
                margin: 0 auto;
                border: 1px solid rgb(61, 61, 61);
                padding: 2%;
                width: 20%;
                .ghostName{
                    color: var(--text-color);
                }
                ul{
                    list-style: none;
                  
                }
                .evidence{
                    color: var(--text-color);
                    margin: 10% 0;
                    width: 100%;
                }
                .false{
                    text-decoration: line-through;
                    opacity: 0.5;
                }
                .true{
                    text-decoration: underline;
                   
                }

            }
        }
    }
}
